import React,{Component} from 'react';
import { Link } from 'react-router-dom';


class AssetHistoryList extends Component{
  constructor(props){
      super(props);
      this.state={
        history:[],
        asset:{
                  "nid": null,
                  "name": null,
                  "deleted": null,
                  "locationText": null,
                  "regDatetime": null,
                  "deleteDatetime": null,
                  "deletionComment": null,
                  "serialNumber": null,
                  "warrantyEndDate": null,
                  "purchaseOrderNumber": null,
                  "purchasePrice": null,
                  "hl7Compatible": null,
                  "onOffDatetime": null,
                  "purchaseDate": null,
                  "commissioningDate": null,
                  "warranty": null,
                  "id": null,
                  "id2": null,
                  "notes": null,
                  "modelText": null,
                  "lastPmDate": null,
                  "pmEnabled": false,
                  "id3": null,
                  "placeNotes": null,
                  "imported": null,
                  "parentNid": null,
                  "locationNid": null,
                  "orgUnitNid": null,
                  "criticityNid": null,
                  "supplierNid": null,
                  "manufacturerNid": null,
                  "stateNid": null,
                  "currentLocationNid": null,
                  "typeNid": null,
                  "brandNid": null,
                  "modelNid": null,
                  "ownershipNid": null,
                  "warrantyStateNid": null,
                  "deleteUsrNid": null,
                  "placeNid": null,
                  "placeLogNid": null,
                  "stateHistoryNid": null,
                  "dropRequestNid": null
            }
      }

    }

    componentDidMount() {

      fetch('https://demo.metaok.com/metacmms_prod/api/inventory/v1/assets/'+this.props.match.params.nid+'/log?fromDate=2018-12-11&toDate=2019-12-11', {
          method: 'GET',
          headers: {
              "Content-Type": "application/json"
          }
      })
        .then(response => response.json())
        .then(data => {
            this.setState({ history: data.data })
          }
        )
        .catch(function(error) {console.log(error);});


        fetch('https://demo.metaok.com/metacmms_prod/api/inventory/v1/assets/'+this.props.match.params.nid, {
            method: 'GET',
            headers: {
                "Content-Type": "application/json"
            }
        })
          .then(response => response.json())
          .then(data => {this.setState({ asset: data.data })
            }
          )
          .catch(function(error) {console.log(error);});

    }

    convertDate(datetime){
      let date=new Date(datetime);
      let formatted_date = date.getDate() + "-" + (date.getMonth() + 1) + "-" + date.getFullYear() + " " + date.getHours() + ":" + date.getMinutes();
      return formatted_date;
    }
    render(){

      console.log(this.state.asset);

      const history=this.state.history.map((request,index)=>{
        let logDatetime=this.convertDate(request.logDatetime);
        return(
          <li className="list-group-item list-group-item-action" key={request.nid} style={{width:'100%'}}>
          <h5>{logDatetime}</h5>
          {request.type.name}
          </li>
        )
      })


      return(
        <div className="container">
          <div className="row row-header bg-success">
            <div className="col-lg">
            Historial del Equipo
            </div>
          </div>
          <div className="row " >
            <div className="col-lg ">
              <ul className="list-group list-group-flush">
              {history}
              </ul>
            </div>
          </div>
          <div className="row" style={{marginTop:'20px'}}>
            <div className="col-md-4" style={{textAlign:'center'}}>
              <Link to="/"><button type="button" className="btn btn-outline-secondary btn-lg btn-full">Regresar</button></Link>
            </div>
          </div>
        </div>
      )
    }
}

export default AssetHistoryList;
