import React,{Component} from 'react';
import { Link } from 'react-router-dom';
import { requests } from '../json/requests.json'
class RequestEditor extends Component{
  constructor(){
      super()
      this.state={
        requests
      }
      this.request={
        'problem':'',
        'motivo':'',
        'urgency_nid':'',
        'asset_nid':''
      }


      this.handleSubmit = this.handleSubmit.bind(this);
      this.handleChange = this.handleChange.bind(this)
    }

    handleChange(e){

       // this.setState({
       //   value: event.target.value
       // });
    }
    handleSubmit(e){
      e.preventDefault();

      console.log(e.target.problem.value);
      console.log(e.target.motivo.value);
      console.log(e.target.urgency_nid.value);

      this.setState({
        problem:e.target.problem.value,
        motivo:e.target.motivo.value,
        urgency_nid:e.target.urgency_nid.value,
      });

      console.log(this.state);

      this.request.motivo=e.target.motivo.value;
      console.log(this.request);


      const data = new FormData(e.target);

      console.log(data);

      this.setState({
        requests:[...this.state.requests,data]
      })
    }

    render(){


      return(
        <div className="container">
          <div className="row row-header">
            <div className="col-lg">
              Respirador Drager
            </div>
            <div className="col-lg">
              En servicio
            </div>
          </div>
          <form onSubmit={this.handleSubmit}>
          <div className="row" style={{textAlign:'center',marginTop:'20px'}}>

            <div className="col-lg">



              <div className="separator">
                <span>Urgencia</span>
              </div>

              <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="urgency_nid" id="urgency_nid" value="1" />
                <label className="form-check-label">Urgente</label>
              </div>
              <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="urgency_nid" id="urgency_nid" value="2" />
                <label className="form-check-label" >Normal</label>
              </div>
              <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="urgency_nid" id="urgency_nid" value="3" />
                <label className="form-check-label" >Baja</label>
              </div>


              <div className="form-group" style={{marginTop:'20px'}}>
                <label >Motivo</label>
                <textarea className="form-control" rows="2" id="motivo" name="motivo"  ></textarea>
              </div>

              <div className="separator">
                <span>Problema</span>
              </div>

              <div className="form-group" >
                <textarea className="form-control" rows="3" id="problem" name="problem" ></textarea>
              </div>

            </div>
          </div>

          <div className="row" style={{marginTop:'20px'}}>
            <div className="col-md-4" style={{textAlign:'center'}}>
              <button className="btn btn-outline-primary btn-lg btn-full" >Enviar</button>
            </div>
          </div>

          </form>

        </div>
      )
    }
}

export default RequestEditor;



