import React,{Component} from 'react';
import { Link } from 'react-router-dom';
import { requests } from '../json/requests.json'
class RequestEditor extends Component{
  constructor(){
      super()
      this.state={
        requests
      }

      this.handleSubmit = this.handleSubmit.bind(this);
      // this.handleChange = this.handleChange.bind(this)
    }

    // handleChange(e){
    //    this.setState({
    //      value: event.target.value
    //    });
    // }
    handleSubmit(e){
      e.preventDefault();
      const data = new FormData(e.target);

      console.log(data)
      this.setState({
        requests:[...this.state.requests,data]
      })
    }

    render(){
      return(
        <div className="container">
          <div className="row row-header">
            <div className="col-lg">
              Respirador Drager
            </div>
            <div className="col-lg">
              En servicio
            </div>
          </div>
          <form onSubmit={this.handleSubmit}>
          <div className="row" style={{textAlign:'center',marginTop:'20px'}}>

            <div className="col-lg">



              <div className="separator">
                <span>Urgencia</span>
              </div>

              <div class="form-check form-check-inline">
                <input class="form-check-input" type="radio" name="urgency_type" id="urgency_type" value="1" />
                <label class="form-check-label" for="inlineRadio1">Urgente</label>
              </div>
              <div class="form-check form-check-inline">
                <input class="form-check-input" type="radio" name="urgency_type" id="urgency_type" value="2" />
                <label class="form-check-label" for="inlineRadio2">Normal</label>
              </div>
              <div class="form-check form-check-inline">
                <input class="form-check-input" type="radio" name="urgency_type" id="urgency_type" value="3" />
                <label class="form-check-label" for="inlineRadio3">Baja</label>
              </div>


              <div className="form-group" style={{marginTop:'20px'}}>
                <label >Motivo</label>
                <textarea className="form-control" rows="2" id="motivo" name="motivo" ></textarea>
              </div>

              <div className="separator">
                <span>Problema</span>
              </div>

              <div className="form-group" >
                <textarea className="form-control" rows="3" id="problem" name="problem" ></textarea>
              </div>

            </div>
          </div>

          <div className="row" style={{marginTop:'20px'}}>
            <div className="col-md-4" style={{textAlign:'center'}}>
              <button className="btn btn-outline-primary btn-lg btn-full" >Enviar</button>
            </div>
          </div>

          </form>

        </div>
      )
    }
}

export default RequestEditor;



