import React,{Component} from 'react';
import { Link } from 'react-router-dom';
class RequestEditor extends Component{
  constructor(){
      super()
      this.state={
        asset:{
                  "nid": null,
                  "stateNid": null,
                  "typeNid": null,
                  "brandNid": null,
                  "modelNid": null,
                  "locationNid": null,
                  "orgUnitNid": null

            }
      }
      this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleSubmit(e){
      e.preventDefault();

      console.log(e.target)

      // 'problem':e.target.problem.value,
      // 'motivo':e.target.motivo.value,
      // 'urgency_nid':e.target.urgency_nid.value,
      // 'asset_nid':''

      let request={
        "changes":{
          "reg_usr_nid": 1,
          "asset_nid": this.state.nid,
          "org_unit_nid": this.state.orgUnitNid,
          "asset_location_nid": this.state.locationNid,
          "asset_state_nid": e.target.motivo.value,
          "urgency_nid": e.target.urgency_nid.value,
          "urgency_reason": e.target.motivo.value,
          "req_nid": 1,
          "problem_text": e.target.problem.value
        }
      }
      console.log(request);
      //
      // fetch('https://demo.metaok.com/metacmms_prod/api/corrective/v1/requests', {
      //     method: 'POST',
      //     body: JSON.stringify(request),
      //     headers: {
      //         "Content-Type": "application/json"
      //     }
      // })
      // .then(response => response.json())
      // .catch(function(error) {console.log(error);});
    }

    componentDidMount() {

      fetch('https://demo.metaok.com/metacmms_prod/api/inventory/v1/assets/'+this.props.match.params.nid, {
          method: 'GET',
          headers: {
              "Content-Type": "application/json"
          }
      })
        .then(response => response.json())
        .then(data => {this.setState({ asset: data.data })
          }
        )
        .catch(function(error) {console.log(error);});
    }

    render(){



      return(
        <div className="container">
          <div className="row row-header  bg-success">
          <div className="col-lg">
            Solicitud MC
          </div>
          <div className="col-lg">
            {this.state.asset.typeNid!=null && this.state.asset.type.name} {this.state.asset.brandNid!=null && this.state.asset.brand.name}
          </div>
          <div className="col-lg">
            {this.state.asset.modelNid!=null && this.state.asset.model.name}
          </div>
          <div className="col-lg">
            {this.state.asset.stateNid!=null && this.state.asset.state.name}
          </div>
          </div>
          <form onSubmit={this.handleSubmit}>
          <div className="row" style={{textAlign:'center',marginTop:'20px'}}>

            <div className="col-lg">

              <div className="separator">
                <span>Urgencia</span>
              </div>

              <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="urgency_nid" id="urgency_nid" value="-3" />
                <label className="form-check-label">Urgente</label>
              </div>
              <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="urgency_nid" id="urgency_nid" value="-2" />
                <label className="form-check-label" >Normal</label>
              </div>
              <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="urgency_nid" id="urgency_nid" value="-1" />
                <label className="form-check-label" >Baja</label>
              </div>


              <div className="form-group" style={{marginTop:'20px'}}>
                <label >Motivo</label>
                <textarea className="form-control" rows="2" id="motivo" name="motivo"  ></textarea>
              </div>

              <div className="separator">
                <span>Problema</span>
              </div>

              <div className="form-group" >
                <textarea className="form-control" rows="3" id="problem" name="problem" ></textarea>
              </div>

              <div className="separator">
                <span>Estado del equipo</span>
              </div>

              <div style={{textAlign:'left'}}>
                <div className="form-check form-check" >
                  <input className="form-check-input" type="radio" name="state_nid" id="state_nid" value="1" />
                  <label className="form-check-label">En servicio</label>
                </div>
                <div className="form-check form-check">
                  <input className="form-check-input" type="radio" name="state_nid" id="state_nid" value="-2" />
                  <label className="form-check-label" >Fuera de Servicio</label>
                </div>
                <div className="form-check form-check">
                  <input className="form-check-input" type="radio" name="state_nid" id="state_nid" value="-3" />
                  <label className="form-check-label" >En Servicio con errores</label>
                </div>
              </div>
            </div>
          </div>

          <div className="row" style={{marginTop:'20px'}}>
            <div className="col-md-4" style={{textAlign:'center'}}>
              <button className="btn btn-outline-primary btn-lg btn-full" onClick={this.handleSubmit}>Enviar</button>
            </div>
            <div className="col-md-4" style={{textAlign:'center',marginTop:'20px'}}>
              <Link to="/"><button type="button" className="btn btn-outline-secondary btn-lg btn-full">Regresar</button></Link>
            </div>
          </div>

          </form>

        </div>
      )
    }
}

export default RequestEditor;
