    componentDidMount() {

      fetch('https://demo.metaok.com/metacmms_prod/api/corrective/v1/requests?stateEnums=RESOLUTION&usrNid=1', {
          method: 'GET',
          headers: {
              "Content-Type": "application/json"
          }
      })
        .then(response => response.json())
        .then(data => {
            this.setState({ resolution: data })
          }
        )
        .catch(function(error) {console.log(error);});

        fetch('https://demo.metaok.com/metacmms_prod/api/corrective/v1/requests?stateEnums=COMPLETED&usrNid=1', {
            method: 'GET',
            headers: {
                "Content-Type": "application/json"
            }
        })
          .then(response => response.json())
          .then(data => {
              this.setState({ complete: data })
            }
          )
          .catch(function(error) {console.log(error);});

          fetch('https://demo.metaok.com/metacmms_prod/api/corrective/v1/requests?stateEnums=CONFORMITY_REQUEST&usrNid=1', {
              method: 'GET',
              headers: {
                  "Content-Type": "application/json"
              }
          })
            .then(response => response.json())
            .then(data => {
                this.setState({ conformity: data })
              }
            )
            .catch(function(error) {console.log(error);});
    }

    render(){

      console.log(this.state.resolution);
      console.log(this.state.conformity);
      console.log(this.state.complete);

      const resolution=this.state.resolution.data.map((request,index)=>{
        return(
          <li className="list-group-item list-group-item-action" key={request.nid} style={{width:'100%'}}>
            <h5>#{request.nid} {request.creation_date} <span className="badge badge-warning">{request.urgency.name}</span></h5>
            {request.asset.name}
          </li>
        )
      })

      const complete=this.state.complete.data.map((request,index)=>{
        return(
          <li className="list-group-item list-group-item-action" key={request.nid} style={{width:'100%'}}>
          <h5>#{request.nid} {request.creation_date} <span className="badge badge-warning">{request.urgency.name}</span></h5>
          {request.asset.name}
          </li>
        )
      })

      const conformity=this.state.conformity.data.map((request,index)=>{
        return(
          <li className="list-group-item list-group-item-action" key={request.nid} style={{width:'100%'}}>
          <h5>#{request.nid} {request.creation_date} <span className="badge badge-warning">{request.urgency.name}</span></h5>
          {request.asset.name}
          </li>
        )
      })

      return(
        <div className="container">
          <div className="row row-header bg-success">
            <div className="col-lg">
            Solicitudes MC
            </div>
          </div>
          <div className="row">

            <ul className="nav nav-tabs" id="myTab" role="tablist" style={{width:'100%'}}>
              <li className="nav-item">
                <a className="nav-link active" id="resolution-tab" data-toggle="tab" href="#resolution" role="tab" aria-controls="resolution" aria-selected="true">Resolucion</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" id="completed-tab" data-toggle="tab" href="#complete" role="tab" aria-controls="complete" aria-selected="false">Completa</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" id="conformity-tab" data-toggle="tab" href="#conformity" role="tab" aria-controls="conformity" aria-selected="false">Conformidad</a>
              </li>
            </ul>
            <div className="tab-content" id="myTabContent" style={{width:'100%'}}>
              <div className="tab-pane fade show active" id="resolution" role="tabpanel" aria-labelledby="resolution-tab">
              <div className="row " >
                <div className="col-lg ">
                  <ul class="list-group list-group-flush">
                  {resolution}
                  </ul>
                </div>
              </div>
              </div>
              <div className="tab-pane fade" id="complete" role="tabpanel" aria-labelledby="complete-tab">
              <div className="row">
                <div className="col-lg">
                  <ul class="list-group list-group-flush">
                  {complete}
                  </ul>
                </div>
              </div>
              </div>
              <div className="tab-pane fade" id="conformity" role="tabpanel" aria-labelledby="conformity-tab">
              <div className="row">
                <div className="col-lg">
                  <ul class="list-group list-group-flush">
                  {conformity}
                  </ul>
                </div>
              </div>
              </div>
            </div>

          </div>
          <div className="row" style={{marginTop:'20px'}}>
            <div className="col-md-4" style={{textAlign:'center'}}>
              <Link to="/"><button type="button" className="btn btn-outline-secondary btn-lg btn-full">Regresar</button></Link>
            </div>
          </div>
        </div>
      )
    }
}

