import React,{Component} from 'react';
import { Link } from 'react-router-dom';


class AssetHistoryList extends Component{
  constructor(props){
      super(props);
      this.state={
        history:[],
        fromDate:null,
        toDate:null,
        asset:{
          "nid": null,
          "stateNid": null,
          "typeNid": null,
          "brandNid": null,
          "modelNid": null
        }
      }

    }

    componentDidMount() {

      fetch('https://demo.metaok.com/metacmms_prod/api/inventory/v1/assets/'+this.props.match.params.nid+'/log?fromDate=2018-12-11&toDate=2019-12-11', {
          method: 'GET',
          headers: {
              "Content-Type": "application/json"
          }
      })
        .then(response => response.json())
        .then(data => {
            this.setState({ history: data.data })
          }
        )
        .catch(function(error) {console.log(error);});


        fetch('https://demo.metaok.com/metacmms_prod/api/inventory/v1/assets/'+this.props.match.params.nid, {
            method: 'GET',
            headers: {
                "Content-Type": "application/json"
            }
        })
          .then(response => response.json())
          .then(data => {this.setState({ asset: data.data })
            }
          )
          .catch(function(error) {console.log(error);});



          this.setState({ fromDate: this.convertDate2(new Date()) });
          this.setState({ toDate: this.convertDate2(new Date()) });


    }

    convertDate(datetime){
      let date=new Date(datetime);
      let formatted_date = date.getDate() + "-" + (date.getMonth() + 1) + "-" + date.getFullYear() + " " + date.getHours() + ":" + date.getMinutes();
      return formatted_date;
    }

    convertDate2(date){
      let formatted_date = date.getFullYear() + "-" + ("0" + (date.getMonth() + 1)).slice(-2) + "-" + ("0" + date.getDate()).slice(-2);
      return formatted_date;
    }
    render(){
      console.log(this.state.fromDate);
      console.log(this.state.toDate);
      
      const history=this.state.history.map((request,index)=>{
        let logDatetime=this.convertDate(request.logDatetime);
        return(
          <li className="list-group-item list-group-item-action" key={request.nid} style={{width:'100%'}}>
          <h5>{logDatetime}</h5>
          {request.type.name}
          </li>
        )
      })

      // this.setState({ from: new Date() }

      // let fromDate =new Date();
      // let toDate=new Date();


      return(
        <div className="container">
          <div className="row row-header bg-success">
            <div className="col-lg">
              Historial del Equipo
            </div>
            <div className="col-lg">
              {this.state.asset.typeNid!=null && this.state.asset.type.name} {this.state.asset.brandNid!=null && this.state.asset.brand.name}
            </div>
            <div className="col-lg">
              {this.state.asset.modelNid!=null && this.state.asset.model.name}
            </div>
            <div className="col-lg">
              {this.state.asset.stateNid!=null && this.state.asset.state.name}
            </div>
          </div>
          <div className="row " >
            <div className="col-lg ">
            <input type="date" id="from" name="from" value={this.state.fromDate}/>
            <input type="date" id="to" name="to" value={this.state.toDate}/>
            </div>
          </div>
          <div className="row " >
            <div className="col-lg ">
              <ul className="list-group list-group-flush">
              {history}
              </ul>
            </div>
          </div>
          <div className="row" style={{marginTop:'20px'}}>
            <div className="col-md-4" style={{textAlign:'center'}}>
              <Link to={{pathname: `/assetEditor/${this.props.match.params.nid}`}}><button type="button" className="btn btn-outline-secondary btn-lg btn-full">Regresar</button></Link>
            </div>
          </div>
        </div>
      )
    }
}

export default AssetHistoryList;
